import React , {useState, useEffect} from "react";
import PopularSeriesPoster from "../Components/PopularSeriesPoster";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import {Container} from "react-bootstrap";
import "../Styles/PopularSeries.css";

function PopularSeries(){

  var settings = {
      dots: false,
      infinite: true,
      speed: 500,
      slidesToShow: 5,
      slidesToScroll: 1,
      responsive: [
          {
            breakpoint: 1024,
            settings: {
              slidesToShow: 3,
              slidesToScroll: 4,
              infinite: true,
              dots: true
            }
          },
          {
            breakpoint: 600,
            settings: {
              slidesToShow: 3,
              slidesToScroll: 2,
              initialSlide: 2
            }
          },
          {
            breakpoint: 480,
            settings: {
              slidesToShow: 3,
              slidesToScroll: 1
            }
        }
      ]
  };

	const [results, setResults] = useState([]);

  useEffect(() => {
  	fetch('https://api.themoviedb.org/3/tv/popular?api_key=c2c2cd525b5005c063f7b1a9d54ab699&language=es-ES&page=1')
  		.then((res) => res.json())
  		.then((data) => {
  	  			console.log(data);
  	  			setResults(data.results);
  	  })
      .catch((error)=>{
        console.log(error)
      })
  }, []);


  return (
    <div className="series-container">
      <h4>Series m√°s populares</h4>
      <Container fluid >
		    <Slider {...settings}> 
          {results.length > 0 && results.map((result)=><PopularSeriesPoster key={result.id} {...result} />)}
		    </Slider> 
      </Container>
	</div>
  );
}

export default PopularSeries;
